import React,{useState} from 'react';
import { Button } from '../Button/ButtonElements';
import { gsap,TimelineLite, Power3} from 'gsap'

import { InfoContainer,
         InfoWrapper,
         InfoRow,
         Column1,
         Column2,
         TextWrapper,
         TopLine,
         Heading,
         Subtitle,
         BtnWrap,
         ImgWrap,
         Img,
          } from "./ShadesElements";

const Shades = ({lightBg,
                      id,
                      imgStart,
                      topLine,
                      lightText,
                      headline,
                      darkText,
                      description,
                      buttonLabel,
                      img,
                      alt,
                      primary,
                      dark,
                      dark2}) => {

    let h1 = new TimelineLite({delay: 0.3});

    useEffect(() => {
        h1.from('ImgWrap', {y: 15, opacity: 0, ease: Power3.easeOut, delay: 0.2}, 'Start')
    }, [])

    return (
        <>
         <InfoContainer lightBg={lightBg} id={id}>
             <InfoWrapper>
                 <InfoRow imgStart={imgStart}>
                    <Column1>
                    <TextWrapper>
                        <TopLine>{topLine}</TopLine>
                        <Heading lightText={lightText}>{headline}</Heading>
                        <Subtitle darkText={darkText}>{description}</Subtitle>
                        <BtnWrap>
                            <Button 
                             to='/signin'
                             smooth={true}
                             duration={500}
                             spy={true}
                             exact="true"
                             offset={-80}
                             primary={primary ? 1 : 0}
                             dark={dark ? 1 : 0}
                             dark2={dark2 ? 1 : 0}
                             color={dark}
                            
                            
                            
                            >{buttonLabel}</Button>
                        </BtnWrap>
                    </TextWrapper>
                    </Column1>
                    <Column2>
                      <ImgWrap>
                        <Img src={img} alt={alt}/>
                      </ImgWrap>
                    </Column2>
                 </InfoRow>
             </InfoWrapper>
         </InfoContainer>   
        </>
    )
}

export default Shades;
