import styled from 'styled-components';

export const NavbarContainer = styled.nav`
    background: ${({scrollNav}) => (scrollNav ? 'transparent' : '#000')};
    height: 50px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.2rem;
    position: sticky;
    z-index: 10;
`
